import React, { useEffect, useState } from 'react';
import '../../resources/Allprojects.css';

import projects from '../../data/projects.json'
import ProjectComponents from '../Projects/projectComponents'


const AllProjects = () => {

    var Wordpress = projects.filter(e => e.cat === "Wordpress")
    var MERN = projects.filter(e => e.cat === "MERN")
    var MEAN = projects.filter(e => e.cat === "MEAN")
    var Frontend= projects.filter(e => e.cat ===  "Front End")
    const [polls, setPolls] = useState(null);
// used for displaying polls and filtering
    const [filteredPolls, setfilteredPolls] = useState(null)

    function showA() {
        setfilteredPolls(Wordpress)
    }

    function showB() {
        setfilteredPolls(MERN)
    }

    function showC() {
        setfilteredPolls(MEAN)
    }
    function showD() {
        setfilteredPolls(Frontend)
    }
    return( <div className="movein">
        <div className="move">        < div className='container'>




                < div className='projects'>

                    <div className="titleWraper">
                        <h2 data-aos="fade-up" data-aos-duration="1000">All Projects</h2>
                        <p className="paraa" data-aos="fade-up" data-aos-duration="2000">All Projects have their open source on github which can be simply review when clicking read more on each project.</p>


                    </div>



            <div style={filterProjects} >
                <div style={filterProjects.bar} className="bar" >

                        <button  className='btnn' onClick={() => showA()}>Wordpress</button>
                        <button    className='btnn' onClick={() => showB()}>MERN</button>
                        <button   className='btnn' onClick={() => showC()}>MEAN</button>
                    <button   className='btnn' onClick={() => showD()}>Frontend</button>


</div>
                      <div> { filteredPolls?
                            filteredPolls && filteredPolls.map((poll) => (
                            <>
                                <ProjectComponents

                                    float={poll.float}
                                    title={poll.title}
                                    id={poll.pId}
                                    content={poll.content}
                                    more={poll.more}
                                    btn={poll.btn}>         </ProjectComponents  >                       </>

                        )):  projects.map((skill)=>(


                          <ProjectComponents

                          float={skill.float}
                          title={skill.title}
                          id={skill.pId}
                          content={skill.content}
                          more={skill.more}
                          btn={skill.btn}



                          >


                          </ProjectComponents





                          >

                          ))}
                    </div>                </div>

            <div style={sort} >
                <div style={{display:'flex',gap:'5px'}}>
                     </div>
            </div>


                </div>
        </div>
</div>

    <div className="overlay"></div>
</div>

)
}

const filterProjects = {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '25px',
    bar:{
        background:'#bdbdbd',
        marginTop:'50px',
         marginRight:'300px',
        width: '65vw',
        height: '50px',
        display: 'flex',
        borderRadius: '150px',
        alignItems: 'center',
        justifyContent: 'space-around',
    },
    cat:{
        display: 'flex',
        alignItems: 'center',
        transition: '.3s all',
        cursor: 'pointer',
    }
}

const sort = {
    margin: '0px auto',
    textAlign: 'center',
    marginBottom: '35px',

}

export default AllProjects
