import React,{useState,useEffect} from "react";
import {AppBar, Toolbar, IconButton, Menu} from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';

const Header = () =>{
    const [headerShow, setHeaderShow]=useState(false);

    const handleScroll = ()=> {
        if (window.scrollY > 0) {
            setHeaderShow(true)
        } else {
            setHeaderShow(false)
        }
    }
    useEffect(()=>{
        window.addEventListener('scroll',handleScroll)
    },[]);
    return(
        <AppBar position="fixed" style={{
            backgroundColor:headerShow ? '#fbf3ec': 'transparent',
            boxShadow:headerShow ?'0 8px 8px -4px #565657':"none",
        }}>


            <Toolbar>



                <IconButton aria-label="Menu" color='inherit'>
                    <div className="header_logo">
                        <div className=" font_righteous header_logo_venue"> The Venue</div>
                        <div className="header_logo_title"> Musical Events</div>

                    </div>
                    <nav>
                        <ul className={`${mobileMenu && 'toggleMobileMenu'}`}>
                            <li className="underline home">
                                <NavLink exact to="/" activeClassName="active"><AiFillHome /> Home</NavLink></li>
                            <li className="underline">
                                <NavLink exact to="/portfolio" activeClassName="active"><AiFillLayout /> Portfolio</NavLink></li>
                            <li onClick={() => scrollTo('contact')} className="underline contact"><AiFillPhone /> Contact</li>
                        </ul>
                    </nav>




                </IconButton>


            </Toolbar>
        </AppBar>


    )
}
export default Header;
