import React, { useEffect, useState } from 'react';
import '../../resources/Allprojects.css';

import projects from '../../data/projects.json'
import ProjectComponents from '../Projects/projectComponents'


const AllProjects = () => {

    var A = projects.filter(e => e.cat === "Wordpress")
    var B = projects.filter(e => e.cat === "MERN")
    var C = projects.filter(e => e.cat === "MEAN")
    const [polls, setPolls] = useState(null);
// used for displaying polls and filtering
    const [filteredPolls, setfilteredPolls] = useState(null)

    function showA() {
        setfilteredPolls(A)
    }

    function showB() {
        setfilteredPolls(B)
    }

    function showC() {
        setfilteredPolls(C)
    }

    return(

        < div className='container'>




                < div className='projects'>

                    <div className="titleWraper">
                        <h2 data-aos="fade-up" data-aos-duration="1000">All Projects</h2>
                        <p className="paraa" data-aos="fade-up" data-aos-duration="2000">All Projects have their open source on github which can be simply review when clicking read more on each project.</p>


                    </div>



            <div style={filterProjects} >
                <div><div style={filterProjects.bar} className="bar" >

                        <button  className='btn' onClick={() => showA()}>A</button>
                        <button    className='btn' onClick={() => showB()}>B</button>
                        <button   className='btn' onClick={() => showC()}>C</button>


</div>
                      <div> {
                            filteredPolls && filteredPolls.map((poll) => (
                            <li>
                                <ProjectComponents

                                    float={poll.float}
                                    title={poll.title}
                                    id={poll.pId}
                                    content={poll.content}
                                    more={poll.more}
                                    btn={poll.btn}



                                >         </ProjectComponents





                                >                       </li>

                        ))}
                    </div>                </div>
            </div>

            <div style={sort} >
                <div style={{display:'flex',gap:'5px'}}>
                     </div>
            </div>


                </div>
        </div>

    )
}

const filterProjects = {
    display: 'flex',
    justifyContent: 'center',
    marginBottom: '25px',
    bar:{
        background:'#bdbdbd',
        marginTop:'50px',
         marginRight:'300px',
        width: '65vw',
        height: '50px',
        display: 'flex',
        borderRadius: '150px',
        alignItems: 'center',
        justifyContent: 'space-around',
    },
    cat:{
        display: 'flex',
        alignItems: 'center',
        transition: '.3s all',
        cursor: 'pointer',
    }
}

const sort = {
    margin: '0px auto',
    textAlign: 'center',
    marginBottom: '35px',

}

export default AllProjects
