import React,{useState,useEffect} from "react";
import {AppBar, Toolbar, IconButton, Menu} from '@material-ui/core';
import { NavLink } from 'react-router-dom';
import { AiFillHome, AiOutlineMenu, AiFillPhone, AiFillLayout,AiOutlineArrowUp } from 'react-icons/ai';
import {Drawer,List, ListItem} from '@material-ui/core'

const Header = () =>{
    const [headerShow, setHeaderShow]=useState(false);
    const [drawerOpen, setDrawerOpen]=useState(false);

    const handleScroll = ()=> {
        if (window.scrollY > 0) {
            setHeaderShow(true)
        } else {
            setHeaderShow(false)
        }
    }
    const nav = {
        display: 'flex',
        width: '100vw',
        height: '50px',
        padding: '25px',
        alignItems: 'center',
        paddingLeft: '50px',
        marginLeft:'60%'

    }
    const toggleDrawer=(value)=>{
        setDrawerOpen(value)
    };

    useEffect(()=>{
        window.addEventListener('scroll',handleScroll)
    },[]);

    return(
        <AppBar position="fixed" style={{
            backgroundColor:headerShow ? '#fbf3ec': 'transparent',
            boxShadow:headerShow ?'0 8px 8px -4px #565657':"none",
        }}>


            <Toolbar>

                <div style={nav} className='Navbar'>

                <nav>
                    <ul >
                        <li className="underline home">
                            <NavLink exact to="/" activeClassName="active"><AiFillHome /> Home</NavLink>

                        </li>
                        <li className="underline">
                            <NavLink exact to="/portfolio" activeClassName="active"><AiFillLayout /> Portfolio</NavLink></li>
                        <li  className="underline contact">   <NavLink exact to="/contact" activeClassName="active"> <AiFillPhone /> Contact</NavLink></li>
                    </ul>

                </nav>

                    <Drawer anchor="right"
                            open={drawerOpen}
                            onClose={(value)=>toggleDrawer(value) }>

                        <List component="nav">
                            <ul >
                                <li className="underline home">
                                    <NavLink exact to="/" activeClassName="active"><AiFillHome /> Home</NavLink>

                                </li>
                                <li className="underline">
                                    <NavLink exact to="/portfolio" activeClassName="active"><AiFillLayout /> Portfolio</NavLink></li>
                                <li  className="underline contact">   <NavLink exact to="/contact" activeClassName="active"> <AiFillPhone /> Contact</NavLink></li>
                            </ul>

                        </List>

                    </Drawer>
                </div>
            </Toolbar>
        </AppBar>


    )



}

export default Header;
