import React from "react";
import {Drawer,List, ListItem} from '@material-ui/core'
import {scroller} from 'react-scroll'
import {NavLink} from "react-router-dom";
import {AiFillHome, AiFillLayout, AiFillPhone} from "react-icons/ai";

const SideDrawer=(props)=>{

    const styles = {
        nav: {
            height: 50,
            background: '#096dd9',
            boxShadow: '0 5px 10px rgba(0, 0, 0, 0.15)',

            alignItems: 'center',
        },
        button: {
            background: 'transparent',
            border: 'none',
            outline: 'none',
            color: 'white',
            fontSize: 16,
        },
    };


    return(
    <Drawer anchor="left"
            style={styles}
            open={props.open}
    onClose={()=> props.onClose(false)}>


           <List style={styles.nav}>

               <li className="underline home">
                   <NavLink exact to="/" activeClassName="active"><AiFillHome /> Home</NavLink>

               </li>
               <li className="underline">
                   <NavLink exact to="/portfolio" activeClassName="active"><AiFillLayout /> Portfolio</NavLink></li>
               <li  className="underline contact">   <NavLink exact to="/contact" activeClassName="active"> <AiFillPhone /> Contact</NavLink></li>

           </List>



    </Drawer>

)


}

export default  SideDrawer;
